def parse():


    # throw each word in a set
    words = set(text.split(" "))
    words.add("bavaria")

    kwm = {
        "germany": {
            "states": [
                "bavaria",
                "badenwürtemberg"
            ]
        },
        "uk": {
            "states": [
                "scotland",
                "wales"
            ]
        },
    }

    def apply_kwm(words, kwm) -> List[str]:
        """
        Returns a list of keywords which were found
        """


        if isinstance(kwm, dict):
            applied = set()
            for keyword, sub_hierarchy in kwm.items():
                sub = apply_kwm(words, sub_hierarchy)
                if sub:
                    applied.add(keyword)
                    applied.update(sub)
            return applied

        elif isinstance(kwm, list):
            for keyword in kwm:
                if keyword in words:
                    return {keyword}

            return {}

        elif isinstance(kwm, str):
            keyword = kwm
            if keyword in words:
                return {keyword}

            return {}


    applied = apply_kwm(words, kwm)
    # print(applied)

import timeit

took = timeit.timeit(parse, number=10000)
print(took)
